INSERT INTO `tbl_closure_email_template` VALUES (9,1,NULL,NULL,NULL,NULL,'<!DOCTYPE html>\n <html>\n \n <body style=\"font-family: \'Arial\', sans-serif; font-size: 16px; color: #4b0082;\">\n     <div>\n         <div>Dear <span style=\"font-weight: bold;\">{UserName}</span>,</div>\n         <div style=\"margin-top: 15px;\">\n             <p>We acknowledge your online request for the closure of your Trading account {Trading} and Demat account {Demat}.</p>\n             <p>To proceed with the closure process, please validate your request by clicking on the link provided below. Generate and validate through OTP for further processing.</p>\n             <p><a href={pageUrl} style=\'color: #4b0082; text-decoration: underline;\'>CLICK HERE</a></p>\n             <p>For any further queries, we kindly request you to get in touch at 1800-425-4477 or write to <u>csecsupport@chola.murugappa.com</u>.</p>\n         </div>\n         <div style=\"margin-top: 15px;\">Sincerely,</div>\n         <div style=\"font-weight: bold; margin-top: 5px;\">CHOLAMANDALAM SECURITIES LIMITED</div>\n     </div><br>\n     <div>\n         <img src=\'https://ekyc.cholasecurities.com/images/Chola-logo.png\' alt=\'Chola Securities Logo\' style=\'max-width: 100%; height: auto; margin-top: 20px;\'>\n     </div>\n </body>\n \n </html>',NULL,'ApprovalClosure','Trading / DP Account Closure Request',NULL,9),(10,1,NULL,NULL,NULL,NULL,'<!DOCTYPE html>\n<html>\n\n<body>\n    <div style=\"color: #4b0082; font-family: \'Arial\', sans-serif; font-size: 16px;\">\n        <div>Dear <span style=\"font-weight: bold;\">{UserName}</span>,</div><br>\n        <div style=\"margin-top: 15px;\">\n            <p>We\'ve received your online request to close your Trading <span style=\"font-weight: bold;\">{Trading}</span> and Demat accounts <span style=\"font-weight: bold;\">{Demat}</span>. Our team will review the documents and update you on the closure status soon.</p>\n        </div>\n        <div style=\"margin-top: 15px;\">For any queries, please reach out to us at 1800-425-4477 or <a href=\'mailto:csecsupport@chola.murugappa.com\'>csecsupport@chola.murugappa.com</a>.</div>\n        <div style=\"margin-top: 15px;\">Sincerely,</div>\n        <div style=\"font-weight: bold; margin-top: 5px;\">CHOLAMANDALAM SECURITIES LIMITED</div>\n    </div><br>\n    <div>\n        <img src=\'https://ekyc.cholasecurities.com/images/Chola-logo.png\' alt=\'Chola Securities Logo\' style=\'max-width: 100%; height: auto; margin-top: 20px;\'>\n    </div>\n</body>\n\n</html>',NULL,'EsignClosure','Trading / DP Account Closure Request',NULL,10),(11,1,NULL,NULL,NULL,NULL,'<!DOCTYPE html>\n<html>\n\n<body>\n    <div style=\'color: #4b0082; font-family: \"Arial\", sans-serif; font-size: 16px;\'>\n        <div>Dear <span style=\'font-weight: bold;\'>{UserName}</span>,</div>\n        <div style=\'margin-top: 15px;\'>\n            <p>Your online request for the closure of Trading {Trading} and Demat account {Demat} has been rejected for the reason {Rejected Reason}.</p>\n            <p>Kindly address the issue and resubmit the closure request through our online portal.</p>\n            <p>For further queries, we kindly request you to get in touch at 1800-425-4477 or write to <u>csecsupport@chola.murugappa.com</u>.</p>\n        </div>\n        <div style=\'margin-top: 15px;\'>Sincerely,</div>\n        <div style=\'font-weight: bold; margin-top: 5px;\'>CHOLAMANDALAM SECURITIES LIMITED</div>\n    </div><br>\n    <div>\n        <img src=\'https://ekyc.cholasecurities.com/images/Chola-logo.png\' alt=\'Chola Securities Logo\' style=\'max-width: 100%; height: auto; margin-top: 20px;\'>\n    </div>\n</body>\n\n</html>\n',NULL,'RejectionClosure','Trading / DP Account Closure Request',NULL,11),(12,1,NULL,NULL,NULL,NULL,'<!DOCTYPE html>\n  <html>\n  \n  <body>\n      <div style=\"color: #4b0082; font-family: \'Arial\', sans-serif; font-size: 16px;\">\n          <div>Dear <span style=\"font-weight: bold;\">{UserName}</span>,</div